<?php\n// Get the rowid from GET request\n$rowid = $_GET['rowid'];\n\n// Split the rowid into an array using a comma as the delimiter\n$dataArray = explode(',', $rowid);\n\n// Assign separated values to variables\n$value1 = $dataArray[0];\n$value2 = $dataArray[1];\n\n// Output the values separately\necho "Value 1: " . htmlspecialchars($value1) . "\n";\necho "Value 2: " . htmlspecialchars($value2);\n?>