// Ensure the file name is safe by sanitizing it.\n$safe_filename = basename($file);\n\n// Set appropriate headers for PDF display and filename.\nheader('Content-Type: application/pdf');\nheader("Content-Disposition: inline; filename=\"" . htmlspecialchars($safe_filename, ENT_QUOTES) . "\"");\nheader("Cache-Control: max-age=0");\nheader("Pragma: public");\n\n// Output the file content.\nreadfile($file);