public function index()\n{\n    if (!$this->session->userdata('logged_in')) {\n        // Check for remember me cookie\n        if ($cookie = $this->input->cookie('auth')) {\n            $user_data_from_cookie = json_decode($cookie, true);\n            $email = $user_data_from_cookie['email'];\n            $password = $user_data_from_cookie['password'];\n\n            $user_data = $this->user_model->login($email, $password);\n\n            if ($user_data) {\n                // Set session data here as above\n            }\n        }\n\n        // redirect to login page\n        redirect('login');\n    } else {\n        redirect('dashboard');\n    }\n}