<?php\n\nfunction xor_obfuscate($id, $key) {\n    // XOR the ID with a key to obfuscate it.\n    return $id ^ $key;\n}\n\nfunction xor_deobfuscate($obfuscated_id, $key) {\n    // XORing again with the same key will revert to original id.\n    return $obfuscated_id ^ $key;\n}\n\n$internal_id = 1234; // Example internal ID\n$key = 0xDEADBEEF;   // Your secret key (can be any integer)\n\n// Obfuscate the ID\n$obfuscated_id = xor_obfuscate($internal_id, $key);\necho "Obfuscated ID: " . dechex($obfuscated_id) . "\n";\n\n// Deobfuscate back to original ID\n$deobfuscated_id = xor_deobfuscate($obfuscated_id, $key);\necho "Deobfuscated ID: " . $deobfuscated_id . "\n";\n?>