function readability($text) {\n    // Remove extra spaces and new lines for accuracy\n    $clean_text = preg_replace('/\s+/', ' ', trim($text));\n\n    // Count sentences more accurately using regular expressions\n    preg_match_all('/[.!?]+/', $clean_text, $sentences);\n    $total_sentences = count($sentences[0]);\n\n    // Count words in the text\n    $total_words = str_word_count($clean_text);\n\n    // Syllable counting is approximate; here's a basic approach\n    $text_low = strtolower($clean_text);\n    preg_match_all('/(?:\b|[aeiouy])[a-z]{1,20}\b/i', $text_low, $matches);\n    $syllables = 0;\n    foreach ($matches[0] as $word) {\n        // Count syllables in each word\n        preg_match_all('/'[bcdfghjklmnpqrstvwxyz]*([aeiouy]+)[bcdfghjklmnpqrstvwxyz]*)*/i', $word, $match);\n        $syllables += count($match[1]);\n    }\n\n    // Ensure we don't divide by zero\n    if ($total_sentences == 0 || $total_words == 0) {\n        return 0;\n    }\n\n    // Calculate the Flesch-Kincaid Readability Score\n    $score = 206.835 - (1.015 * ($total_words / $total_sentences)) - (84.6 * ($syllables / $total_words));\n    return round($score);\n}