<?php\n$data = "Your sensitive data here";\n\n// Load public key from file or define inline\n$publicKeyFile = 'path/to/public_key.pem';\n$fp = fopen($publicKeyFile, "r");\n$pub_key = fread($fp, 8192);\nfclose($fp);\n\nopenssl_get_publickey($pub_key);\n\n// Encrypt the data using the public key\nopenssl_public_encrypt($data, $encryptedData, $pub_key, OPENSSL_PKCS1_PADDING);\n\n// Encode so it can be sent via URL\n$base64Encrypted = base64_encode($encryptedData);\n\n// Use GET or POST to send the encrypted data\necho "Sending data...";\nfile_get_contents("https://remote-server.com/api.php?data=$base64Encrypted");\n?>