<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Retrieve data sent via AJAX\n    $param1 = filter_input(INPUT_POST, 'param1', FILTER_SANITIZE_STRING);\n\n    // Create XML content\n    $xmlContent = "<?xml version='1.0' encoding='UTF-8'?>\n";\n    $xmlContent .= "<data>\n";\n    $xmlContent .= "  <item>$param1</item>\n"; // Use the received data here\n    $xmlContent .= "</data>";\n\n    // Write XML content to a file\n    $filePath = 'output.xml';\n    if (file_put_contents($filePath, $xmlContent) !== false) {\n        echo "XML file successfully written.";\n    } else {\n        echo "Failed to write the XML file.";\n    }\n}\n?>