<?php\n$encryptedData = base64_decode($_GET['data']);\n\n// Load private key from file or define inline\n$privateKeyFile = 'path/to/private_key.pem';\n$fp = fopen($privateKeyFile, "r");\n$priv_key = fread($fp, 8192);\nfclose($fp);\n\nopenssl_get_privatekey($priv_key);\n\n// Decrypt the data using the private key\nopenssl_private_decrypt($encryptedData, $decryptedData, $priv_key, OPENSSL_PKCS1_PADDING);\n\necho "Received and decrypted data: " . $decryptedData;\n?>