function timeAgo($timestamp) {\n    $currentTime = new DateTime();\n    $givenTime = new DateTime("@$timestamp");\n    $interval = $currentTime->diff($givenTime);\n\n    $years = $interval->y;\n    $months = $interval->m;\n    $days = $interval->d;\n    $hours = $interval->h;\n\n    // Construct the output string\n    $output = '';\n    if ($years > 0) {\n        $output .= "$years year" . ($years > 1 ? 's' : '') . ' ';\n    }\n    if ($months > 0) {\n        $output .= "$months month" . ($months > 1 ? 's' : '') . ' ';\n    }\n    if ($days > 0) {\n        $output .= "$days day" . ($days > 1 ? 's' : '') . ' ';\n    }\n    if ($hours > 0 && $days < 1) {\n        $output .= "$hours hour" . ($hours > 1 ? 's' : '');\n    }\n\n    return trim($output) . " ago";\n}\n\n// Example usage\n$timestamp = strtotime('-3 days -5 hours'); // Replace with your timestamp\necho timeAgo($timestamp);