<?php\n// Assume $userId is obtained from session or authentication\n$targetDirectory = __DIR__ . '/pics/';\n$uploadPath = '';\nif (isset($_FILES['image'])) {\n    $fileType = mime_content_type($_FILES['image']['tmp_name']);\n    if ($fileType === 'image/jpeg' || $fileType === 'image/png') { // Validate file type\n        $uuid = uniqid(); // Generate a UUID\n        $extension = pathinfo($_FILES['image']['name'], PATHINFO_EXTENSION);\n        $newFileName = $targetDirectory . $uuid . '.' . $extension;\n        if (move_uploaded_file($_FILES['image']['tmp_name'], $newFileName)) {\n            $uploadPath = $newFileName;\n            // Store $uploadPath and related metadata in your database\n        } else {\n            echo 'Failed to upload file.';\n        }\n    } else {\n        echo 'Invalid file type. Only JPEG or PNG files are allowed.';\n    }\n}\n?>