require_once 'XML/Serializer.php';\n\n$serializer = new XML_Serializer();\n$serializer->setOption('rootName', 'Request'); // Set the root element name as needed\n$serializer->setOption('indent', '   '); // Indentation for pretty print\n\n$result = $serializer->serialize($requestArray);\n\nif ($result === true) {\n    echo $serializer->getSerializedData();\n} else {\n    die("Serialization Error: " . $serializer->getLastError());\n}