// Assuming this is in your PHP file\n<?php\nclass ProductController {\n    public function getIndex() {\n        // Example SQL query using PDO, emulating fetching data from a database\n        $pdo = new PDO('mysql:host=localhost;dbname=mystore', 'user', 'pass');\n        $stmt = $pdo->query("SELECT * FROM products");\n        $products = $stmt->fetchAll();\n\n        // This would normally be passed to a view in an MVC setup\n        return json_encode($products);\n    }\n}