$itemList = array();\n$i = 0;\n$xmlReader = new XMLReader();\n$xmlReader->open('XMLFILE');\nwhile ($xmlReader->read()) {\n    if ($xmlReader->nodeType == XMLReader::ELEMENT) {\n        if ($xmlReader->localName == 'title') {\n            $xmlReader->read(); \n            $itemList[$i]['title'] = $xmlReader->value;\n        }\n        if ($xmlReader->localName == 'description') {\n            // move to its textnode / child\n            $xmlReader->read(); \n            $itemList[$i]['description'] = $xmlReader->value; \n\n        } \n        if ($xmlReader->namespaceURI === "http://search.yahoo.com/mrss/" && $xmlReader->localName == 'thumbnail') {\n            // Get the URL attribute of media:thumbnail\n            $itemList[$i]['media:thumbnail_url'] = $xmlReader->getAttribute('url');\n            $itemList[$i]['media:thumbnail_width'] = $xmlReader->getAttribute('width');\n            $itemList[$i]['media:thumbnail_height'] = $xmlReader->getAttribute('height');\n        }\n    } elseif ($xmlReader->nodeType == XMLReader::END_ELEMENT && $xmlReader->localName == 'item') {\n        // Move to the next item\n        $i++;\n    }\n}\n$xmlReader->close();