require 'vendor/autoload.php';\n\nuse PhpOffice\PhpWord\IOFactory;\n\n// Load your RTF file\n$rtfFile = 'path/to/your/document.rtf';\n$templateProcessor = IOFactory::load($rtfFile);\n\n// Save as HTML\n$htmlFilePath = '/tmp/output.html';\n$objWriter = IOFactory::createWriter($templateProcessor, 'HTML');\n$objWriter->save($htmlFilePath);\n\n// Read content and split by page breaks (assuming `\f` is used for form feed/page break)\n$htmlContent = file_get_contents($htmlFilePath);\n$pages = preg_split('/<div[^>]*style="page-break-after: always;"[^>]*><\/div>/i', $htmlContent, -1, PREG_SPLIT_NO_EMPTY);\n\n// Output each page\nforeach ($pages as $index => $page) {\n    echo "<div class='page'>Page " . ($index + 1) . ":<br>" . $page . "</div><hr>";\n}