<?php\nerror_reporting(E_ALL | E_STRICT);\n\nclass Joe {\n    private $properties = [];\n\n    public function __get($prop) {\n        if (isset($this->properties[$prop])) {\n            return $this->properties[$prop];\n        }\n        trigger_error("Undefined property: " . get_class($this) . "::\${$prop}", E_USER_ERROR);\n    }\n\n    public function __set($prop, $value) {\n        if (property_exists(__CLASS__, $prop)) {\n            $this->$prop = $value;\n        } else {\n            trigger_error("Setting undefined property: " . get_class($this) . "::\${$prop}", E_USER_ERROR);\n        }\n    }\n}\n\n$joe = new Joe();\n// This will throw an error because 'lastNombre' is not a declared property\n$joe->lastNombre = "Smith"; \n\n// This will also throw an error for undefined variable $jose\necho "And here it is " . $jose;\n?>