<?php\n$result = mysql_query("SHOW TABLES FROM feather") or die(mysql_error());\n\nwhile (list($table) = mysql_fetch_row($result)) {\n    // Validate that the table name is not empty and does not contain invalid characters\n    if (!empty($table) && preg_match('/^[a-zA-Z0-9_]+$/', $table)) {\n        $sqlA = "SELECT COUNT(*) FROM `$table` WHERE `site` LIKE '$entry'";\n        $resA = mysql_query($sqlA) or die("$sqlA: " . mysql_error());\n        list($isThere) = mysql_fetch_row($resA);\n        if ($isThere > 0) {\n            $table_list[] = $table;\n        }\n    } else {\n        echo "Invalid table name detected: $table\n";\n    }\n}\n\nmysql_free_result($result); // Free the result set\n?>