<?php\ntry {\n    $pdo = new PDO("mysql:host=localhost;dbname=feather", "username", "password");\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    foreach ($pdo->query("SHOW TABLES") as $row) {\n        $table = $row[0];\n        if (!empty($table) && preg_match('/^[a-zA-Z0-9_]+$/', $table)) {\n            $stmt = $pdo->prepare("SELECT COUNT(*) FROM `$table` WHERE `site` LIKE :entry");\n            $stmt->execute([':entry' => "%" . $entry . "%"]);\n            if ($stmt->fetchColumn() > 0) {\n                $table_list[] = $table;\n            }\n        } else {\n            echo "Invalid table name detected: $table\n";\n        }\n    }\n} catch (PDOException $e) {\n    die("PDO Error: " . $e->getMessage());\n}\n?>