<?php\n// Load the two images\n$background = imagecreatefrompng('background.png');\n$foreground = imagecreatefrompng('overlay.png');\n\n// Get the dimensions of the background image\n$bgWidth = imagesx($background);\n$bgHeight = imagesy($background);\n\n// Get the dimensions of the foreground image\n$fGWidth = imagesx($foreground);\n$fGHeight = imagesy($foreground);\n\n// Merge the foreground onto the background at position (0, 0)\nimagecopy($background, $foreground, 0, 0, 0, 0, $fGWidth, $fGHeight);\n\n// Save the result to a new file\nimagepng($background, 'merged.png');\n\n// Free up memory\nimagedestroy($background);\nimagedestroy($foreground);\n?>