<?php\n\nfunction dms_to_decimal($degrees, $minutes, $seconds, $direction) {\n    $decimal = $degrees + ($minutes / 60) + ($seconds / (60 * 60));\n    if ($direction == 'W' || $direction == 'S') {\n        $decimal *= -1;\n    }\n    return $decimal;\n}\n\n$dms_latitude = [37, 14, 6, 'N'];\n$dms_longitude = [115, 48, 40, 'W'];\n\n$latitude_decimal = dms_to_decimal($dms_latitude[0], $dms_latitude[1], $dms_latitude[2], $dms_latitude[3]);\n$longitude_decimal = dms_to_decimal($dms_longitude[0], $dms_longitude[1], $dms_longitude[2], $dms_longitude[3]);\n\necho "Latitude in Decimal Degrees: $latitude_decimal\n";\necho "Longitude in Decimal Degrees: $longitude_decimal\n";\n\n?>