<?php\n// Create a new DOMDocument instance\n$dom = new DOMDocument();\n$dom->formatOutput = true;\n\n// Create root element\n$root = $dom->createElement('Worksheet');\n$dom->appendChild($root);\n\n// Create Cell and Data elements\n$cell = $dom->createElement('Cell');\n$data = $dom->createElement('Data');\n\n// Append cell to root\n$root->appendChild($cell);\n// Append data to cell\n$cell->appendChild($data);\n\n// Create a text node with the content including line breaks as &#10;\n$textNode = $dom->createTextNode("text&#10;text2&#10;");\n\n// Append text node to Data element\n$data->appendChild($textNode);\n\n// Save XML to screen or file\necho $dom->saveXML();\n?>