// Set default timezone to UTC to avoid DST issues\ndate_default_timezone_set('UTC');\n\nfunction convertMySQLDateToTimestamp($mysqlDT) {\n    list ($year, $month, $day) = explode ('-', $mysqlDT);\n    // Convert MySQL date to timestamp in UTC\n    return mktime (0, 0, 0, $month, $day, $year);\n}\n\n// Example usage\n$dateEnrolledTs = convertMySQLDateToTimestamp($dateEnrolled);\n$resultDateTs = convertMySQLDateToTimestamp($resultDate);\n$certDateTs = convertMySQLDateToTimestamp($certDate);\n\necho "e.dateEnrolled = new Date({$dateEnrolledTs}000);\n";\nif ($dateCompleted != -1) {\n    $dateCompletedTs = convertMySQLDateToTimestamp($dateCompleted);\n    echo "e.dateCompleted = new Date({$dateCompletedTs}000);\n";\n} else {\n    echo "e.dateCompleted = new Date(-1000);\n";\n}\necho "e.resultDate = new Date({$resultDateTs}000);\n";\necho "e.certDate = new Date({$certDateTs}000);\n";