<?php\n// Convert .Rd file to Html and extract metadata\nfunction convertRdToHtml($rdFilePath) {\n    // Use Rscript to convert Rd to HTML or another tool you prefer\n    $html = shell_exec("R CMD Rd2html $rdFilePath");\n    \n    // Extract keywords, title, description etc. using regex or any other method\n    preg_match('/\\item{Title}{(.*?)}/', $html, $titleMatch);\n    preg_match_all('/\\keyword{(.*?)}/', $html, $keywordsMatch);\n    \n    // Create metadata array\n    $metadata = [\n        'path' => basename($rdFilePath),\n        'title' => trim(@$titleMatch[1]),\n        'keywords' => @$keywordsMatch[1],\n    ];\n    \n    return ['html' => $html, 'metadata' => $metadata];\n}\n\n// Example usage: convert a directory of files\n$files = glob("path/to/rd/files/*.Rd");\n$allMetadata = [];\n\nforeach ($files as $file) {\n    $result = convertRdToHtml($file);\n    // Write the HTML output to a file\n    file_put_contents('html/' . basename($file, '.Rd') . '.html', $result['html']);\n    \n    // Store metadata\n    $allMetadata[] = $result['metadata'];\n}\n\n// Save all metadata as JSON for client-side use\nfile_put_contents('data/metadata.json', json_encode($allMetadata));\n?>