<?php\n// Assuming you already have the raw email body in a variable $rawEmail\n$data = mailparse_msg_parse($rawEmail);\n$structure = mailparse_msg_get_structure($data);\n\nforeach ($structure as $index) {\n    $part = mailparse_msg_get_part($data, $index);\n    $ctype = mailparse_rfc822_parse_headers(mailparse_msg_get_part_data($part)->headers);\n\n    if (strtolower($ctype['content-type']) == 'text/plain') {\n        $bodyData = stream_get_contents($ctype['fp']);\n        \n        // Example regex to find some specific data\n        preg_match('/SomePatternHere: ([^\s]+)/', $bodyData, $matches);\n        $foundData = isset($matches[1]) ? trim($matches[1]) : '';\n\n        // Create a new email with the parsed data in the subject\n        require 'PHPMailerAutoload.php';\n\n        $mail = new PHPMailer;\n        $mail->isSMTP();\n        $mail->Host = 'smtp.yourserver.com';\n        $mail->SMTPAuth = true;\n        $mail->Username = 'yourusername';\n        $mail->Password = 'yourpassword';\n        $mail->setFrom('from@example.com', 'Your Name');\n        $mail->addAddress('to@example.com', 'Recipient Name');\n\n        // Set the subject with parsed data\n        $mail->Subject = "New Subject: {$foundData}";\n        $mail->Body    = strip_tags($bodyData);\n        $mail->AltBody = $bodyData;\n\n        if (!$mail->send()) {\n            echo 'Message could not be sent.';\n            echo 'Mailer Error: ' . $mail->ErrorInfo;\n        } else {\n            echo 'Message has been sent';\n        }\n\n        break;\n    }\n}\n?>