<?php\n// Load the GIF image\n$image = imagecreatefromgif('path/to/your/image.gif');\n\n// Define colors\n$newWhite = imagecolorallocate($image, 0, 0, 0); // Black color\n$newLineColor = imagecolorallocate($image, 173, 216, 230); // Light blue color (sky blue)\n\n// Get the dimensions of the image\n$width = imagesx($image);\n$height = imagesy($image);\n\n// Define the colors to be swapped\n$currentColorWhite = imagecolorallocate($image, 255, 255, 255); // White RGB\n\n// Loop through each pixel and swap colors\nfor ($x = 0; $x < $width; $x++) {\n    for ($y = 0; $y < $height; $y++) {\n        $currentColor = imagecolorat($image, $x, $y);\n        \n        // Check if the current color is white and replace it with black\n        if ($currentColor == $currentColorWhite) {\n            imagesetpixel($image, $x, $y, $newWhite);\n        }\n        // Additional conditions can be added for other colors if needed\n    }\n}\n\n// Save the modified image\nimagegif($image, 'path/to/your/modified_image.gif');\n\n// Free up memory\nimagedestroy($image);\n?>