function validateForm(array $formData): bool {\n    // Filter out elements with empty values\n    $filteredData = array_filter($formData, function($value) {\n        return !empty($value);\n    });\n\n    // Compare counts to see if any values were considered empty\n    return count($filteredData) === count($formData);\n}\n\n// Example usage:\n$formData = [\n    'name' => 'John Doe',\n    'email' => 'john.doe@example.com',\n    'phone' => ''  // This will cause the validation to fail\n];\n\nif (!validateForm($formData)) {\n    echo "Please fill in all required fields.";\n} else {\n    echo "Form is valid, proceed with processing.";\n}