function getFirstDayOfWeek($locale) {\n    $calendar = new IntlGregorianCalendar(NULL, $locale);\n    // Get the first day of the week (1=MONDAY, 2=TUESDAY, ..., 7=SUNDAY)\n    $firstDayOfWeek = $calendar->getFirstDayOfWeek();\n    \n    return $firstDayOfWeek;\n}\n\n// Example usage\n$locale = 'en_US';\necho getFirstDayOfWeek($locale); // Outputs: 1 (SUNDAY in ICU's representation)\n\n$locale = 'en_GB';\necho getFirstDayOfWeek($locale); // Outputs: 2 (MONDAY)