<?php\n\nclass Config {\n    private static $config = null;\n\n    // Function to load configuration from the database into an array once per request\n    public static function loadConfig() {\n        if (self::$config === null) {\n            $conn = new mysqli('host', 'user', 'password', 'database');\n            if ($conn->connect_error) {\n                die("Connection failed: " . $conn->connect_error);\n            }\n\n            // Assuming a table called 'site_config' with columns 'name' and 'value'\n            $result = $conn->query("SELECT name, value FROM site_config");\n            self::$config = array();\n\n            while ($row = $result->fetch_assoc()) {\n                self::$config[$row['name']] = $row['value'];\n            }\n\n            $conn->close();\n        }\n    }\n\n    // Function to get a config value\n    public static function get($key) {\n        if (!isset(self::$config)) {\n            self::loadConfig();\n        }\n\n        return self::$config[$key] ?? null;\n    }\n}\n\n// Usage example\n$configValue = Config::get('results_per_page');\n?>