<?php\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$searchTerm = "example";\n\n// Prepare and execute a single SELECT statement.\n$stmt = $pdo->prepare("SELECT * FROM your_table WHERE column_name = :search_term");\n$stmt->execute([':search_term' => $searchTerm]);\n\n// Fetch the results\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\nprint_r($results);\n?>