// Example: Method with optional parameter\nclass UtilityClass {\n    public function sayHello($name = "Guest") {\n        return "Hello, " . $name;\n    }\n}\n\nclass TestUtility extends PHPUnit\Framework\TestCase {\n    public function testSayHelloWithDefault() {\n        $util = new UtilityClass();\n        $this->assertEquals("Hello, Guest", $util->sayHello());\n    }\n\n    public function testSayHelloWithName() {\n        $util = new UtilityClass();\n        $this->assertEquals("Hello, Alice", $util->sayHello('Alice'));\n    }\n}