// Example: Testing a method that processes form input\nclass FormProcessor {\n    public function process($formData) {\n        // Process the form data and return result\n        if (!isset($formData['name'])) {\n            throw new Exception("Name is required.");\n        }\n        return "Processed Name: " . htmlspecialchars($formData['name']);\n    }\n}\n\nclass TestFormProcessor extends PHPUnit\Framework\TestCase {\n    public function testProcessValidData() {\n        $processor = new FormProcessor();\n        $this->assertEquals("Processed Name: John Doe", $processor->process(['name' => 'John Doe']));\n    }\n\n    public function testProcessMissingName() {\n        $processor = new FormProcessor();\n        $this->expectException(Exception::class);\n        $processor->process([]);\n    }\n}