<?php\n\n$string = '<p>I have a CMS with a WYSIWYG editor which produces pretty good xhtml.</p>' .\n          '<ul><li>Item 1</li><li>Item 2</li></ul>' .\n          '<ol><li>Point A</li><li>Point B</li></ol>';\n\n// Regex pattern to match content within <p>, <ul>, and <ol> tags\n$pattern = '/<(p|ul|ol)>(.*?)<\/\\1>/s';\n\npreg_match_all($pattern, $string, $matches);\n\nforeach ($matches[0] as $match) {\n    echo htmlspecialchars($match) . "\n";\n}\n\n?>