<?php\n\nfunction getVisitCount($filename) {\n    $filePath = $_SERVER['DOCUMENT_ROOT'] . '/uploads/' . basename($filename);\n    $countFilePath = $_SERVER['DOCUMENT_ROOT'] . '/uploads/' . basename($filename) . '.visit_count';\n    \n    if (!file_exists($countFilePath)) {\n        file_put_contents($countFilePath, 0); // Initialize visit count\n    }\n    return (int)file_get_contents($countFilePath);\n}\n\nfunction incrementVisitCount($filename) {\n    $filePath = $_SERVER['DOCUMENT_ROOT'] . '/uploads/' . basename($filename);\n    $countFilePath = $_SERVER['DOCUMENT_ROOT'] . '/uploads/' . basename($filename) . '.visit_count';\n    \n    $visitCount = getVisitCount($ filePath);\n    file_put_contents($countFilePath, $visitCount + 1); // Increment visit count\n}\n\nfunction viewFile($filename) {\n    $filePath = $_SERVER['DOCUMENT_ROOT'] . '/uploads/' . basename($filename);\n    $visitCount = getVisitCount($filename);\n\n    if ($visitCount >= 5) {\n        echo '<h1>Link has expired.</h1>';\n    } else {\n        incrementVisitCount($filename);\n        readfile($filePath); // Display the file content\n    }\n}\n\nif (isset($_GET['filename'])) {\n    viewFile($_GET['filename']);\n} elseif (isset($_POST['execute'])) {\n    execute_upload();\n} else {\n    display_upload_form();\n}