<?php\nuse setasign\Fpdi\Fpdi;\n\n$pdf = new Fpdi();\n\n// Add a page from an existing PDF file\n$pageCount = $pdf->setSourceFile('template.pdf');\n$tplIdx = $pdf->importPage(1);\n$pdf->AddPage();\n$pdf->useTemplate($tplIdx, 0, 0);\n\n// Replace these values with the actual coordinates and dimensions you obtain.\n$x = 50; // X position\n$y = 75; // Y position\n$width = 40;\n$height = 10;\n\n// Example: adding text to a specific field or position\n$pdf->SetFont('Helvetica');\n$pdf->SetXY($x, $y);\n$pdf->Cell($width, $height, 'Sample Text');\n\n// Output the FPDF with populated fields\n$pdf->Output();