class PostsController extends AppController {\n    public function index() {\n        // $this refers to the current instance of PostsController\n        // ->Post accesses the Post model associated with this controller\n        // ->find('all') calls the find method on the Post model to retrieve all records\n        $posts = $this->Post->find('all');\n        $this->set(compact('posts'));\n    }\n}