<?php\n// Open a SQLite database with a persistent connection\n$db = sqlite_popen('mydatabase.db', 0666, $error);\n\nif (!$db) {\n    die("Could not open database: $error");\n}\n\n// Perform your queries here\n$query = "SELECT * FROM users";\n$result = sqlite_query($db, $query);\nwhile ($row = sqlite_fetch_array($result)) {\n    print_r($row);\n}\n\n// Note: There's no need to explicitly close the connection\n?>