function detectFileEncoding($filePath) {\n    // Open the file for reading\n    $file = fopen($filePath, 'r');\n    \n    if (!$file) {\n        throw new Exception("Unable to open file: " . $filePath);\n    }\n    \n    // Read some amount of bytes to detect encoding\n    $content = fread($file, 4096);\n    \n    fclose($file);\n    \n    // Detect the encoding\n    return mb_detect_encoding($content, ['UTF-8', 'ISO-8859-1', 'ASCII'], true);\n}\n\ntry {\n    $filePath = '/path/to/your/file.txt';\n    echo "File Encoding: " . detectFileEncoding($filePath);\n} catch (Exception $e) {\n    echo $e->getMessage();\n}