use Elasticsearch\ClientBuilder;\n\n// Create an instance of the client with default settings\n$client = ClientBuilder::create()->build();\n\n/**\n * Indexing Data Example\n */\n$params = [\n    'index' => 'your_index_name',\n    'body'  => [\n        'title' => 'Your Document Title',\n        'content' => 'This is your document content'\n    ]\n];\n\n$response = $client->index($params);\n\n/**\n * Searching Example with Fuzzy Matching and Boosting\n */\n$searchParams = [\n    'index' => 'your_index_name',\n    'body'  => [\n        'query' => [\n            'bool' => [\n                'should' => [\n                    ['match' => ['title' => ['query' => 'user query', 'fuzziness' => 'AUTO']]],\n                    ['match' => ['content' => ['query' => 'user query', 'boost' => 2.0]]]\n                ]\n            ]\n        ]\n    ]\n];\n\n$searchResults = $client->search($searchParams);\nprint_r($searchResults);