// Install the Twilio PHP SDK via Composer: composer require twilio/sdk\n\nrequire_once 'vendor/autoload.php';\n\nuse Twilio\Rest\Client;\n\n$sid    = "your_account_sid"; // Your Account SID from www.twilio.com/console\n$token  = "your_auth_token";  // Your Auth Token from www.twilio.com/console\n$client = new Client($sid, $token);\n\ntry {\n    $message = $client->messages->create(\n        '+1234567890', // Text this number\n        [\n            'from' => '+0987654321', // From a valid Twilio number\n            'body' => 'Hello from PHP!'\n        ]\n    );\n    echo "Sent message to {$message->to}";\n} catch (Exception $e) {\n    echo "Error: The sending process failed with the following error: " . $e->getMessage();\n}