function convertUnsignedToSigned($unsignedInt) {\n    // Check if the number is considered negative in 32-bit signed representation\n    if ($unsignedInt & 0x80000000) {\n        // Convert to signed by subtracting 2^32\n        return $unsignedInt - 0x100000000;\n    }\n    return $unsignedInt;\n}\n\n// Example usage:\n$unsignedValue = 3232240316; // This is the unsigned representation of a negative number\n$signedValue = convertUnsignedToSigned($unsignedValue);\necho $signedValue; // Outputs: -1062726980