<?php\n\n// Assuming $data is an array of records fetched from your database\n$data = [\n    ['id' => 1, 'name' => 'Item 1', 'value' => 'Value 1'],\n    ['id' => 2, 'name' => 'Item 2', 'value' => 'Value 2'],\n    ['id' => 1, 'name' => 'Item 3', 'value' => 'Value 3'],\n];\n\n$xml = new SimpleXMLElement('<root/>');\n\n$idGroupedData = [];\nforeach ($data as $item) {\n    if (!isset($idGroupedData[$item['id']])) {\n        $idGroupedData[$item['id']] = [];\n    }\n    $idGroupedData[$item['id']][] = $item;\n}\n\nforeach ($idGroupedData as $id => $items) {\n    $groupNode = $xml->addChild('group');\n    foreach ($items as $item) {\n        $child = $groupNode->addChild('item');\n        $child->addChild('name', htmlspecialchars($item['name']));\n        $child->addChild('value', htmlspecialchars($item['value']));\n        // You can add more child elements here based on your data structure\n    }\n}\n\necho $xml->asXML();\n\n?>