<?php\n$string = "This is string sample - $2565";\n$split_point = " - ";\n\n// Splitting the string into two parts using explode\n$parts = explode($split_point, $string);\n\n// Grabs the number after the dollar sign if present\npreg_match('/\$(\d+)/', $string, $matches);\n$numberAfterDollar = isset($matches[1]) ? $matches[1] : null;\n\necho "First part: " . $parts[0] . "\n";\necho "Second part: " . trim($parts[1]) . "\n"; // Using trim to remove any extra spaces\necho "Number after dollar sign: " . $numberAfterDollar . "\n";\n?>