<?php\n$id = isset($_GET['id']) ? intval($_GET['id']) : 0;\n\n// Use prepared statements instead of mysql_query for security reasons.\n$conn = new mysqli("host", "username", "password", "database");\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$stmt = $conn->prepare("SELECT * FROM topics WHERE id = ?");\n$stmt->bind_param("i", $id);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$row = $result->fetch_assoc();\n$title = htmlspecialchars($row['title']);\n$text = bbcode($row['text']);\n\n$replies = [];\n$q = $conn->prepare("SELECT * FROM replies WHERE topic_id = ?");\n$q->bind_param("i", $id);\n$q->execute();\n$resultReplies = $q->get_result();\n\nwhile ($r = $resultReplies->fetch_assoc()) {\n    $replies[] = [\n        'text' => bbcode($r['text']),\n        'date' => htmlspecialchars($r['date']),\n        'poster' => htmlspecialchars($r['poster']),\n        'edited' => htmlspecialchars($r['edited'])\n    ];\n}\n\n// Pass data to the view\ninclude 'view/topic.php';\n$conn->close();\n?>