<?php\n\n$doc = new DOMDocument();\nlibxml_set_errors_handling(false); // Disable default error handling\n$doc->load('http://ws.audioscrobbler.com/2.0/artist/beatles/images.rss');\nlibxml_clear_errors(); // Clear any previous errors\n\n$mediaNamespace = 'http://search.yahoo.com/mrss/'; // Define the media namespace\n\n$arrFeeds = [];\n\nforeach ($doc->getElementsByTagName('item') as $node) {\n    $titleNode = $node->getElementsByTagName('title')->item(0);\n    $descriptionNode = $node->getElementsByTagName('description')->item(0);\n    $linkNode = $node->getElementsByTagName('link')->item(0);\n    $pubDateNode = $node->getElementsByTagName('pubDate')->item(0);\n\n    // Extracting main elements\n    $title = ($titleNode !== null) ? $titleNode->nodeValue : '';\n    $description = ($descriptionNode !== null) ? $descriptionNode->nodeValue : '';\n    $link = ($linkNode !== null) ? $linkNode->nodeValue : '';\n    $pubDate = ($pubDateNode !== null) ? $pubDateNode->nodeValue : '';\n\n    // Extracting media:content and media:thumbnail\n    $mediaContentNode = $node->getElementsByTagNameNS($mediaNamespace, 'content')->item(0);\n    $mediaThumbnailNode = $node->getElementsByTagNameNS($mediaNamespace, 'thumbnail')->item(0);\n\n    $mediaContentUrl = ($mediaContentNode !== null) ? $mediaContentNode->getAttribute('url') : '';\n    $mediaThumbnailUrl = ($mediaThumbnailNode !== null) ? $mediaThumbnailNode->getAttribute('url') : '';\n\n    // Building the array with all info\n    $itemRSS = [\n        'title' => htmlspecialchars($title, ENT_QUOTES, 'UTF-8'),\n        'desc' => htmlspecialchars($description, ENT_QUOTES, 'UTF-8'),\n        'link' => htmlspecialchars($link, ENT_QUOTES, 'UTF-8'),\n        'date' => $pubDate,\n        'mediaContentUrl' => htmlspecialchars($mediaContentUrl, ENT_QUOTES, 'UTF-8'),\n        'mediaThumbnailUrl' => htmlspecialchars($mediaThumbnailUrl, ENT_QUOTES, 'UTF-8')\n    ];\n\n    // Add to the array\n    $arrFeeds[] = $itemRSS;\n}\n\n// Output or process the feeds as needed\nprint_r($arrFeeds);\n\n?>