function fetch_url($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n    // Optionally follow redirects if the server returns a redirect code\n    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);\n\n    $response = curl_exec($ch);\n    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n\n    if(curl_errno($ch)) {\n        echo 'cURL error: ' . curl_error($ch);\n        return null;\n    } else {\n        echo "HTTP Code: ". $http_code . "\n"; // Debug line to see the HTTP code\n        curl_close($ch);\n        if ($http_code == 202) {\n            echo "Request accepted but not yet processed.\n";\n            // Handle the case where response is 202 or any other specific handling needed\n        }\n        return $response;\n    }\n}\n\n$url = 'http://###.##.##.##/mp/get?mpsrc=http://mybucket.s3.amazonaws.com/11111.mpg&mpaction=convert format=flv';\n$query = fetch_url($url);\necho($query);