<?php\n$connection = mysqli_connect("host", "username", "password", "database");\n\nif (!$connection) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n$sql = "SELECT * FROM users ORDER BY id";\n$result = mysqli_query($connection, $sql);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    echo "<p>Name: " . htmlspecialchars($row['name']) . "</p>";\n    echo "<p>Email: " . htmlspecialchars($row['email']) . "</p>";\n\n    $dir = "C:/xampp/htdocs/freehosting/" . htmlspecialchars($row['name']);\n    $diskSpace = disk_total_space($dir);\n\n    if ($diskSpace !== false) {\n        echo "<p>Disk Space Available: " . floor($diskSpace / (1024 * 1024)) . " MB</p>";\n    } else {\n        echo "<p>Could not determine disk space for the directory.</p>";\n    }\n}\n\nmysqli_close($connection);\n?>