<?php\nfunction processData($data) {\n    // Process the data\n    foreach ($data as &$item) {  // Use reference to avoid copying the array elements\n        $item = strtoupper($item);\n    }\n    unset($item);  // Break the reference\n\n    return $data;\n}\n\n// Example usage\n$largeDataArray = range('a', 'z'); // Simulating a large dataset\n$processedData = processData($largeDataArray);\n\n// Unset variables to free up memory when done with them\nunset($largeDataArray);\n?>