<?php\n\n$total_time = 348;\n$increment = 4;\n$converted_percentage = 0;\n\necho "Starting conversion...\n";\n\nwhile ($converted_seconds < $total_time) {\n    $progress_length = 25; // Length of the progress bar\n    $filled = round(($converted_seconds / $total_time) * $progress_length);\n    $unfilled = $progress_length - $filled;\n    \n    $progress_bar = '|' . str_repeat('=', $filled) . str_repeat(' ', $unfilled) . '|';\n    $converted_percentage = round(($converted_seconds / $total_time) * 100);\n\n    echo "\rConversion: $converted_seconds/$total_time Seconds   $progress_bar  $converted_percentage%"; // \r moves cursor to start of line\n\n    flush(); // Ensure the output is sent to the console immediately\n    usleep(500000); // Sleep for half a second before continuing to simulate work being done\n    \n    $converted_seconds += $increment;\n}\n\necho "\nConversion completed!\n";\n\n?>