<?php\n// Connect to the database\n$servername = "localhost";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch all content blocks\n$sql = "SELECT * FROM content_blocks ORDER BY id ASC";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        echo "<form method='post'>";\n        echo "<input type='hidden' name='id' value='" . $row['id'] . "'>";\n        echo "Block Name: <input type='text' name='block_name' value='" . htmlspecialchars($row['block_name']) . "'><br>";\n        echo "Title: <textarea name='title'>" . htmlspecialchars($row['title']) . "</textarea><br>";\n        echo "Description: <textarea name='description'>" . htmlspecialchars($row['description']) . "</textarea><br>";\n        echo "<input type='submit' name='update' value='Update'>";\n        echo "<input type='submit' name='delete' value='Delete'>";\n        echo "</form><br>";\n    }\n} else {\n    echo "No blocks found.";\n}\n\necho "<h2>Add New Block</h2>";\necho "<form method='post'>";\necho "Block Name: <input type='text' name='new_block_name'><br>";\necho "Title: <textarea name='new_title'></textarea><br>";\necho "Description: <textarea name='new_description'></textarea><br>";\necho "<input type='submit' name='add' value='Add New Block'>";\necho "</form>";\n\n// Handle form submissions\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    if (isset($_POST['update'])) {\n        $id = $_POST['id'];\n        $block_name = mysqli_real_escape_string($conn, htmlspecialchars($_POST['block_name']));\n        $title = mysqli_real_escape_string($conn, htmlspecialchars($_POST['title']));\n        $description = mysqli_real_escape_string($conn, htmlspecialchars($_POST['description']));\n        $update_sql = "UPDATE content_blocks SET block_name='$block_name', title='$title', description='$description' WHERE id=$id";\n        if ($conn->query($update_sql) === TRUE) {\n            echo "Record updated successfully<br>";\n        } else {\n            echo "Error updating record: " . $conn->error;\n        }\n    }\n\n    if (isset($_POST['delete'])) {\n        $id = $_POST['id'];\n        $delete_sql = "DELETE FROM content_blocks WHERE id=$id";\n        if ($conn->query($delete_sql) === TRUE) {\n            echo "Record deleted successfully<br>";\n        } else {\n            echo "Error deleting record: " . $conn->error;\n        }\n    }\n\n    if (isset($_POST['add'])) {\n        $new_block_name = mysqli_real_escape_string($conn, htmlspecialchars($_POST['new_block_name']));\n        $new_title = mysqli_real_escape_string($conn, htmlspecialchars($_POST['new_title']));\n        $new_description = mysqli_real_escape_string($conn, htmlspecialchars($_POST['new_description']));\n        $add_sql = "INSERT INTO content_blocks (block_name, title, description) VALUES ('$new_block_name', '$new_title', '$new_description')";\n        if ($conn->query($add_sql) === TRUE) {\n            echo "New record created successfully<br>";\n        } else {\n            echo "Error: " . $sql . "<br>" . $conn->error;\n        }\n    }\n}\n\n$conn->close();\n?>