<?php\n// Your large data\n$largeData = "This is quite a lot of data and it can be very long...";\n\n// Initialize a cURL session\n$ch = curl_init();\n\n// Set the URL where you want to send the POST request\ncurl_setopt($ch, CURLOPT_URL, "http://example.com/get.php");\n\n// Set options for a POST request\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query(array('data' => $largeData)));\n\n// Execute the request and capture the response\n$response = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n}\n\n// Close the cURL session\ncurl_close($ch);\n\n// Output the server's response\necho $response;\n?>