<?php\n\nclass Main {\n    public $vars = array();\n}\n\nclass Extended extends Main {\n    function __construct(Main $main) {\n        // Copy the vars property from the main instance to this instance\n        $this->vars = $main->vars;\n    }\n}\n\n// Create a new Main object and set some variables\n$main = new Main;\n$main->vars['key'] = 'value';\n\n// Pass the $main object to the Extended class constructor\n$other = new Extended($main);\n\n// Output the vars array from the Extended class\nvar_dump($other->vars); // Outputs: array(1) { ["key"]=> string(5) "value" }\n\n?>